import React, { Component } from "react";

export default class Event_Binding extends Component {
  handleClickMe = () => {
    alert("Yes");
  };
  handleSayHello = (name) => {
    console.log(name);
  };
  render() {
    return (
      <div>
        <p>Event_Binding</p>
        <button onClick={this.handleClickMe} className="btn btn-success">
          ClickMe
        </button>
        <button onClick={this.handleSayHello} className="btn btn-primary">
          Say Hello
        </button>
      </div>
    );
  }
}
