import { datashoesArr } from "../../Data_Shoes";
import { VIEW_DETAIL } from "../constants/shoesConstants";

export let initialState = {
  shoesArr: datashoesArr,
  shoesdetail: datashoesArr[0],
  cartShoes: [],
};

export let shoesReducer = (state = initialState, action) => {
  switch (action.type) {
    case VIEW_DETAIL: {
      state.shoesdetail = action.payload;
      return { ...state };
    }
    default:
      return state;
  }
};
