import React, { Component } from "react";
import CartShoe from "./CartShoe";
import { datashoesArr } from "./data_shoes";
import Detail_Shoes from "./Detail_Shoes";
import ListShoes from "./ListShoes";

export default class Ex_ShoesShop extends Component {
  state = {
    shoesArr: datashoesArr,
    shoesdetail: datashoesArr[0],
    CartShoe: [],
  };
  handleViewDetail = (idShoes) => {
    let index = this.state.shoesArr.findIndex((item) => {
      return item.id == idShoes.id;
    });
    index != -1 && this.setState({ shoesdetail: this.state.shoesArr[index] });
  };
  handleAddShoesCard = (shoes) => {
    let index = this.state.shoesArr.findIndex((item) => {
      return (item.id = shoes.id);
    });
    let cloneCartShoes = [...this.state.CartShoe];
  };
  render() {
    return (
      <div>
        <CartShoe />
        <ListShoes
          data={this.state.shoesArr}
          handleViewDetail={this.handleViewDetail}
        />
        <Detail_Shoes shoesdetail={this.state.shoesdetail} />
      </div>
    );
  }
}
