import React, { Component } from "react";
import { connect } from "react-redux";
import {
  decreaseNumberAction,
  increaseNumberAction,
} from "./Redux/actions/numberActions";
import { INCREASE_NUMBER } from "./Redux/constants/numberConstants";

class DemoReduxMini extends Component {
  render() {
    return (
      <div className="text-center py-5">
        <button
          onClick={() => {
            this.props.decreaseNumber(10);
          }}
          className="btn btn-danger btn-lg"
        >
          -
        </button>
        <span className="display-4 text-secondary mx-3">
          {this.props.number}
        </span>
        <button
          onClick={this.props.increaseNumber}
          className="btn btn-success btn-lg"
        >
          +
        </button>
      </div>
    );
  }
}

let mapStateToProps = (state) => {
  //mapStateToProps: lấy dữ liệu về dưới dạng props
  return {
    number: state.number.soLuong,
    isLogin: state.number.isLogin,
  };
};

let mapDispatchToProps = (dispatch) => {
  //mapDispatchToProps: đẩy dữ liệu lên store
  return {
    increaseNumber: () => {
      let action = {
        type: INCREASE_NUMBER,
      };
    },
    decreaseNumber: (value) => {
      dispatch(decreaseNumberAction(value));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(DemoReduxMini);
