import React, { Component } from "react";
import CartShoes from "./CartShoes";
import { datashoesArr } from "./Data_Shoes";
import Detail_Shoes from "./Detail_Shoes";
import ListShoes from "./ListShoes";

export default class Ex_ShoesShopRedux extends Component {
  state = {
    shoesArr: datashoesArr,
    shoesdetail: datashoesArr[0],
    cartShoes: [],
  };

  handleAddShoesCard = (shoes) => {
    let cloneCartShoes = [...this.state.cartShoes];
    let index = this.state.cartShoes.findIndex((item) => {
      return item.id == shoes.id;
    });
    if (index == -1) {
      //th sản phẩm chưa có trong giỏ hàng
      let productCart = { ...shoes, amount: 1 };
      cloneCartShoes.push(productCart);
    } else {
      //th sản phẩm đã có trong giỏ hàng
      cloneCartShoes[index].amount++;
    }
    this.setState({ cartShoes: cloneCartShoes });
  };
  handleDeleteShoes = (shoes) => {
    let cloneCartShoes = [...this.state.cartShoes];
    let index = this.state.cartShoes.findIndex((item) => {
      return item.id == shoes.id;
    });
    cloneCartShoes.splice(index, 1);
    this.setState({ cartShoes: cloneCartShoes });
  };
  handleIncreaseAmount = (shoes) => {
    let cloneCartShoes = [...this.state.cartShoes];
    let index = this.state.cartShoes.findIndex((item) => {
      return item.id == shoes.id;
    });
    cloneCartShoes[index].amount++;
    console.log("cloneCartShoes[index]: ", cloneCartShoes[index]);
    this.setState({ cartShoes: cloneCartShoes });
  };
  handleDecreaseAmount = (shoes) => {
    let cloneCartShoes = [...this.state.cartShoes];
    let index = this.state.cartShoes.findIndex((item) => {
      return item.id == shoes.id;
    });
    if (cloneCartShoes[index].amount > 1) {
      this.setState({ cartShoes: cloneCartShoes });
      return cloneCartShoes[index].amount--;
    } else {
      cloneCartShoes.splice(index, 1);
      this.setState({ cartShoes: cloneCartShoes });
    }
  };
  render() {
    return (
      <div>
        <CartShoes
          cartShoes={this.state.cartShoes}
          handleDeleteShoes={this.handleDeleteShoes}
          handleDecreaseAmount={this.handleDecreaseAmount}
          handleIncreaseAmount={this.handleIncreaseAmount}
        />
        <ListShoes
          data={this.state.shoesArr}
          handleAddShoesCard={this.handleAddShoesCard}
        />
        <Detail_Shoes />
      </div>
    );
  }
}
