import React, { Component } from "react";
import UserInfo_Prop from "./UserInfo_Prop";

export default class Demo_Prop extends Component {
  //set Object
  state = {
    username: "Alice",
    age: 2,
  };
  //set function
  //   handleChangeName = () => {
  //     this.setState({ username: "Bob" });
  //   };
  handleChangeName = () => {
    this.setState({ username: "Bob" });
  };
  render() {
    return (
      <div>
        <p>Demo_Prop</p>
        <UserInfo_Prop
          username={this.state.username}
          userage={this.state.age}
          handleChangeName={this.handleChangeName}
        />
      </div>
    );
  }
}
