import React, { Component } from "react";

export default class Demo_State extends Component {
  state = {
    isLogin: true,
  };
  handleLogin = () => {
    this.setState({
      isLogin: true,
    });
  };
  handleLogout = () => {
    this.setState({
      isLogin: false,
    });
  };
  render() {
    return (
      <div>
        {this.state.isLogin ? (
          <button onClick={this.handleLogout} className="btn btn-warning">
            Logout
          </button>
        ) : (
          <button onClick={this.handleLogin} className="ml-2 btn btn-success">
            Login
          </button>
        )}
      </div>
    );
  }
}
