let initialState = {
  soLuong: 100,
  isLogin: true,
};

export let numberReducer = (state = initialState, action) => {
  switch (action.type) {
    case "increaseNumber": {
      state.soLuong++;
      return { ...state };
    }
    case "decreaseNumber": {
      state.soLuong -= action.payload;
    }
    default: {
      return state;
    }
  }
};
