import React, { Component } from "react";

export default class Demo_State extends Component {
  state = {
    isLogin: true,
    name: "Alice",
    amount: 1,
  };
  handleLogin = () => {
    this.setState({
      isLogin: true,
    });
  };
  handleLogout = () => {
    this.setState({
      isLogin: false,
    });
  };
  handleIncrease = () => {
    this.setState({
      amount: this.state.amount + 1,
    });
  };
  render() {
    return (
      <div>
        {this.state.isLogin ? (
          <button onClick={this.handleLogout} className="btn btn-warning">
            Logout
          </button>
        ) : (
          <button onClick={this.handleLogin} className="btn btn-success">
            Login
          </button>
        )}
        {/* nút tăng giảm số lượng */}
        <div className="mt-5">
          <button className="btn btn-success">-</button>
          <span className="mx-1">{this.state.amount}</span>
          <button onClick={this.handleIncrease} className="btn btn-warning">
            +
          </button>
        </div>
      </div>
    );
  }
}
