import React, { Component } from "react";

export default class Item_Shoes extends Component {
  render() {
    let { name } = this.props.detail;
    let { image } = this.props.detail;
    let { price } = this.props.detail;

    return (
      <div className="col-3">
        <div className="card" style={{ width: "18rem" }}>
          <img className="card-img-top" src={image} alt="Card image cap" />
          <div className="card-body">
            <h5 className="card-title">{name}</h5>
            <p className="card-text">{price}</p>
            <button className="btn btn-sm btn-secondary">Add to Card</button>
            <button className="btn btn-sm btn-warning">More detail</button>
          </div>
        </div>
      </div>
    );
  }
}
