import { DECREASE_NUMBER, INCREASE_NUMBER } from "../constants/numberConstants";

let initialState = {
  soLuong: 100,
  isLogin: true,
};

export let numberReducer = (state = initialState, action) => {
  switch (action.type) {
    case INCREASE_NUMBER: {
      state.soLuong++;
      return { ...state };
    }
    case DECREASE_NUMBER: {
      state.soLuong -= action.payload;
      return { ...state };
    }
    default: {
      return state;
    }
  }
};
