import React, { Component } from "react";
import UserInfo_Prop from "./UserInfo_Prop";

export default class Demo_Prop extends Component {
  state = {
    username: "Alice",
    age: 2,
  };
  render() {
    return (
      <div>
        <p>Demo_Prop</p>
        <UserInfo_Prop username={this.state.username} />
      </div>
    );
  }
}
