import React, { Component } from "react";

export default class Event_Binding extends Component {
  handleClickMe = () => {
    alert("Yes");
  };
  handleSayHello = (name) => {
    alert(name);
  };

  render() {
    return (
      <div>
        <p>Event_Binding</p>
        <button onClick={this.handleClickMe} className="btn btn-success">
          ClickMe
        </button>
        <button
          onClick={() => {
            this.handleSayHello("Alice");
          }}
          className="ml-5 btn btn-primary"
        >
          Say Hello
        </button>
      </div>
    );
  }
}
